mapper.select:
		SELECT DINING_ID diningId, DINING_NAME diningName, DINING_TYPE diningType, HALL_TABLE hallTable, ROOM_TABLE roomTable, OPEN_TIME openTime, CLOSE_TIME closeTime, LOCATION, DEPOSIT, DINING_IMG diningImg, DINING_COMMENT diningComment, MENU
    	FROM DINING
    	WHERE DELETE_STATUS = 'N' AND DINING_ID = #{diningId}

SELECT 


            AVAILABLE_HALL_TABLE


            AVAILABLE_ROOM_TABLE


FROM DINING_SEATS
WHERE DINING_ID = #{diningId, jdbcType=VARCHAR} 
AND DINING_DATE = #{date, jdbcType=DATE}
AND DINING_TIME = #{part, jdbcType=INTEGER}
