mapper.select:
SELECT member_id AS userId
FROM member
WHERE exit_flag = 'N'

    AND kakao_id = #{socialId, jdbcType=VARCHAR}


    AND google_id = #{socialId, jdbcType=VARCHAR}


    AND naver_id = #{socialId, jdbcType=VARCHAR}

mapper.update:
update member


            kakao_id = #{socialId}


            google_id = #{socialId}


            naver_id = #{socialId}


    where member_id = #{userId}
    and exit_flag = 'N'

mapper.select:
update member
set password = #{ userPw, jdbcType=VARCHAR }
where member_id = #{ userId, jdbcType=VARCHAR } and exit_flag = 'N'

mapper.update:
update member
set LOGIN_DATE = sysdate
where member_id = #{ userId }

		update member
		set LOGIN_DATE = sysdate,
    		login_method = #{loginMethod, jdbcType=VARCHAR}
		where member_id = #{userId, jdbcType=VARCHAR}
