mapper.select:
SELECT ROOM_ID AS roomId
FROM (
    SELECT R.*,
           ROW_NUMBER() OVER (PARTITION BY R.ROOM_RANK_CODE, R.VIEW_CODE, R.BED_CODE ORDER BY R.ROOM_ID) AS rn
    FROM ROOM R
    WHERE R.ROOM_ID NOT IN (
        SELECT RR.ROOM_ID
        FROM ROOM_RESERVATION RR
        WHERE (RR.CHECKIN < TO_DATE(#{ckoutDate}, 'YYYY-MM-DD') AND RR.CHECKOUT > TO_DATE(#{ckinDate}, 'YYYY-MM-DD'))
           OR (RR.CHECKIN <= TO_DATE(#{ckinDate}, 'YYYY-MM-DD') AND RR.CHECKOUT >= TO_DATE(#{ckoutDate}, 'YYYY-MM-DD'))
    )
) Subquery
WHERE rn = 1
and ROOM_RANK_CODE = #{roomRankCode}
and VIEW_CODE = #{viewCode}
and BED_CODE = #{bedCode}
